import { Box, Button, TextField } from "@mui/material";
import Image from "next/image";
import React, { useState } from "react";

import FileUploader from "../../UI/FileUploader";

import { IAlbumCreateDto } from "../../../types/album";

interface ICreateAlbumFormProps {
  setAlbumInputData: React.Dispatch<React.SetStateAction<IAlbumCreateDto>>;
  albumInputData: IAlbumCreateDto;
}

const CreateAlbumForm: React.FC<ICreateAlbumFormProps> = ({
  setAlbumInputData,
  albumInputData,
}) => {
  const [imageBlob, setImageBlob] = useState<string>('');

  const loadAnImage = (image: any) => {
    setAlbumInputData((prev) => ({ ...prev, image }))
    setImageBlob(URL.createObjectURL(image));
  }

  const onChangeAlbumData = (e: React.ChangeEvent<HTMLInputElement>) => {
    const inputKey = e.target.id === 'newAlbumTitle' ? 'name' : 'description';

    setAlbumInputData((prev) => ({ ...prev, [inputKey]: e.target.value }))
  }

  return (
    <Box display="flex" height="100%" alignItems="center" justifyContent="center" p={5}>
      <Box mr={4} display="flex" flexDirection="column" alignItems="center">
        {imageBlob
          ? (
            <Image src={imageBlob} width={150} height={150} alt="track preview image" />
          )
          : (
            <Box className="imageAltBlock">
              <Image
                width={150}
                height={150}
                alt="no image"
                src="data:image/png;base64,"
              />
            </Box>
          )
        }
        <FileUploader accept="image/*" setFile={loadAnImage}>
          <Button>
            Load an image
          </Button>
        </FileUploader>
      </Box>
      <Box ml={4} width="100%">
        <Box mb={2}>
          <TextField
            id="newAlbumTitle"
            label="Album title"
            value={albumInputData.name}
            onChange={onChangeAlbumData}
          />
        </Box>
        <TextField
          id="newAlbumDescription"
          label="Album description"
          value={albumInputData.description}
          onChange={onChangeAlbumData}
          fullWidth
          multiline
          rows={4}
        />

      </Box>
    </Box>
  )
}

export default CreateAlbumForm;
